mapper.insert:
INSERT INTO mento_profile (
  mentor_idx, user_idx, mentor_tel, sc_idx, mentor_intro, mentor_link
) VALUES (
  mentor_idx.NEXTVAL,
  #{user_idx},
  #{mentor_tel},
  #{sc_idx},
  #{mentor_intro},
  #{mentor_link}
)

mapper.select:
SELECT mentor_idx
FROM mento_profile
WHERE user_idx = #{value}

SELECT COUNT(*)
FROM mentoring_match
WHERE mentoring_idx = #{mentoring_idx}
  AND mentee_user_idx = #{mentee_user_idx}
