mapper.select:
SELECT * FROM (SELECT rownum as rnum, a.* FROM ( SELECT study.*, usertb.user_name, study_category.sc_name FROM study JOIN usertb ON study.user_idx=usertb.user_idx JOIN study_category ON study_category.sc_idx = study.sc_idx
WHERE study_category.sc_idx=#{sc_idx}
WHERE study_title LIKE '%' || #{studyFind} || '%'
ORDER BY study_idx desc ) a ) b WHERE rnum between #{start_num} and #{end_num}

SELECT count(*)
FROM study

		WHERE sc_idx=#{sc_idx}

SELECT user_name, user_email, user_tel
FROM usertb
WHERE user_idx=#{user_idx}

SELECT count(*)
FROM study JOIN usertb ON study.user_idx=usertb.user_idx
WHERE study.user_idx=#{user_idx}

select user_idx
from study 
where study_idx = #{study_idx}
