mapper.select:
select ticket_category_idx, ticket_category_name, studycafe_idx
from ticket_category
where studycafe_idx = #{studycafe_idx}

select ticket_amount
from studycafe_ticket
where ticket_idx = #{ticket_idx}

select ticket_time
from studycafe_ticket
where ticket_idx = #{ticket_idx}

select seat_idx, seat_num, seat_type, seat_status, seat_x, seat_y, seat_w, seat_h, studycafe_idx
from seat
where studycafe_idx = #{studycafe_idx}

select paymentId, storeId, orderName, paid, user_idx, pay_method, pay_status, statusChangedAt, paidAt, totalAmount, pgProvider, vat, supply
from studycafe_pay
where paymentId = #{paymentId}
