mapper.select:
SELECT user_pw
FROM usertb
WHERE user_id = #{user_id}

SELECT count(*)
FROM usertb
WHERE user_id = #{user_id}

SELECT count(*)
FROM usertb
WHERE user_email = #{user_email}

mapper.update:
UPDATE usertb
SET user_pw = #{user_pw}
WHERE user_id =
#{user_id}

UPDATE usertb
SET user_pw = #{new_pw}
WHERE user_idx = #{user_idx}

UPDATE usertb
SET user_name = #{user_name},
    user_birthdate = #{user_birthdate},
    user_tel = #{user_tel},
    user_email = #{user_email}
WHERE user_idx = #{user_idx}

UPDATE mypage
SET user_intro = #{user_intro},
    profile_img = #{profile_img}
WHERE user_idx = #{user_idx}
